/**
 * 병사 배치하기
 *  n = 병사의 수 (1 <= n <= 2000)
 *  str = 병사의 전투력
 */

export function arraySoldier(n, str) {
  const arr = str.split(" ");

  const result = arr.reduce((sum, soldier, i) => {
    const nextSoldier = arr[i + 1];
    if (Number(soldier) < Number(nextSoldier)) {
      return sum + 1;
    }

    return sum;
  }, 0);

  console.log(result);
  return result;
}

function no(n, str) {
  const soldierApArr = str.split(" ");
  const arr = new Array(n).fill(1);

  for (let i = 1; i < n; i++) {
    for (let j = 0; j < i; j++) {
      if (Number(soldierApArr[j]) > Number(soldierApArr[i])) {
        arr[i] = Math.max(arr[i], arr[j] + 1);
      }
    }
  }

  console.log(arr);
}

export default arraySoldier;
