/**
 * 1로 만들기
 *  1 <= x <= 30,000
 */

function makeItOne(x) {
  // 0을 제외
  const arr = new Array(x + 1).fill(0);
  arr[1] = 1;

  for (let i = 2; i < x + 1; i++) {
    arr[i] = arr[i - 1];

    if (i % 2 === 0) {
      arr[i] = Math.min(arr[i], arr[i / 2]);
    }

    if (i % 3 === 0) {
      arr[i] = Math.min(arr[i], arr[i / 3]);
    }

    if (i % 5 === 0) {
      arr[i] = Math.min(arr[i], arr[i / 5]);
    }
  }

  console.log(arr);
  return arr;
}

export default makeItOne;
