// 3 <= n <= 100
// 0 <= k <= 1000

// n = 4, arr = [1, 3, 1, 5]
export default function soldierAnts(n, arr) {
  let result = 0;

  for (let i = 0; i < n - 1; i++) {
    if (arr[i] > result) {
      result = arr[i];
    }

    for (let j = i + 2; j < n; j++) {
      console.log(arr[i] + arr[j], result);
      if (arr[i] + arr[j] > result) {
        result = arr[i] + arr[j];
      }
    }
  }

  // console.log(result);

  return;
}
