/**
 * 병사 배치하기
 *  n = 병사의 수 (1 <= n <= 2000)
 *  str = 병사의 전투력
 */

function arraySoldier(n, str) {
  const arr = str.split(" ");

  const result = arr.reduce((sum, soldier, i) => {
    const nextSoldier = arr[i + 1];
    if (Number(soldier) < Number(nextSoldier)) {
      return sum + 1;
    }
  }, 0);

  console.log(result);
}

export default arraySoldier;
