/**
 * 금광
 *  T = 테스트 케이스의 개수 (1 <= T <= 1000)
 *  nm = 금광 넓이 (1 <= n, m <= 20)
 *  count = 각 금광에 매장된 금의 개수 (1 <= count <= 100)
 */

function goldMine(T, nm, count) {
  const [n, m] = nm.split(" ");
  const arr = count.split(" ").reduce((acc, cur, index) => {
    const accIndex = index / n;
    if (acc[accIndex]) {
      acc[accIndex] = [];
    }

    console.log(acc);
    if (acc[accIndex]?.length === n) {
      return [...acc, [cur]];
    }

    return [...acc, [...acc[accIndex], cur]];
  }, []);

  return "";
}

export default goldMine;
