function goldMine(T, nm, count) {
  const [n, m] = nm.split(" ");
  const countArr = count.split(" ");
  const arr = [];

  for (let i = 0; i < countArr.length / n; i++) {
    const startIndex = i * n;
    arr.push(countArr.slice(startIndex, startIndex + Number(n)));
  }

  for (let i = 1; i < m + 1; i++) {
    for (let j = 0; j < n; j++) {
      let leftDown = arr[i - 1][j - 1];
      let left = arr[i - 1][j];
      let leftUp = arr[i - 1][j + 1];

      if (j === 0) {
        leftUp = 0;
      }
      if (j === n - 1) {
        leftDown = 0;
