/**
 * 1로 만들기
 *  1 <= x <= 30,000
 */

function makeItOne(x) {
  const arr = new Array(x + 1).fill(0);
  for (let i = 2; i < x + 1; i++) {
    arr[i] = arr[i - 1] + 1;

    if (arr[i] % 2 === 0) {
      console.log([arr[i], arr[i / 2] + 1]);
      arr[i] = Math.min(arr[i], arr[i / 2] + 1);
    }

    if (arr[i] % 3 === 0) {
      arr[i] = Math.min(arr[i], arr[i / 3] + 1);
    }

    if (arr[i] % 5 === 0) {
      arr[i] = Math.min(arr[i], arr[i / 5] + 1);
    }
  }
}

export default makeItOne;
