// 식량 털기(개미 전사)
// n은 창고의 개수 (3 <= n <= 100)
// k는 식량의 양 (0<= k <= 1000)

function dp(n, arr) {
  let d = new Array(n).fill(0);

  d[0] = arr[0];
  d[1] = Math.max(arr[0], arr[1]);

  console.time("dp");
  for (let i = 2; i < n; i++) {
    d[i] = Math.max(d[i - 1], d[i - 2] + arr[i]);
    console.log(i, d);
  }

  console.log(d[n - 1]);

  console.timeEnd("dp");
  return d[n - 1];
}

export const soldierAnts = {
  dp,
};

