/**
 * 금광
 *  T = 테스트 케이스의 개수 (1 <= T <= 1000)
 *  nm = 금광 넓이 (1 <= n, m <= 20)
 *  count = 각 금광에 매장된 금의 개수 (1 <= count <= 100)
 */

function goldMine(T, nm, count) {
  const [n, m] = nm.split(" ");
  const countArr = count.split(" ");
  const arr = [];

  for (let i = 0; i < countArr.length / n; i++) {
    const startIndex = i * n;
    const endIndex = (i + 1) * n - 1;

    arr.push(countArr.slice(startIndex, endIndex));
  }

  console.log(arr);

  return "";
}

export default goldMine;
