// 식량 털기(개미 전사)
// n은 창고의 개수 (3 <= n <= 100)
// k는 식량의 양 (0<= k <= 1000)

function soldierAnts(n, arr) {
  let d = new Array(n).fill(0);

  d[0] = arr[0];
  d[1] = Math.max(arr[0], arr[1]);

  for (let i = 2; i < n; i++) {
    d[i] = Math.max(d[i - 1], d[i - 2] + arr[i]);
  }

  return d[n - 1];
}

export default { soldierAnts };

// i = 2, d = [1, 2, 1+2, 0, 0, 0, 0, 0, 0, 0]
// i = 3, d = [1, 2, 3, 2+3, 0, 0, 0, 0, 0, 0]
