/**
 * 금광
 *  T = 테스트 케이스의 개수 (1 <= T <= 1000)
 *  nm = 금광 넓이 (1 <= n, m <= 20)
 *  count = 각 금광에 매장된 금의 개수 (1 <= count <= 100)
 */

function goldMine(T, nm, count) {
  const [n, m] = nm.split(" ");
  const countArr = count.split(" ");
  const arr = [];
  const intN = Number(n);
  const intM = Number(m);

  for (let i = 0; i < countArr.length / m; i++) {
    const startIndex = i * m;
    arr.push(countArr.slice(startIndex, startIndex + intM));
  }

  for (let j = 1; j < intM; j++) {
    for (let i = 0; i < intN; i++) {
      // console.log(arr, i, j, arr[i][j]);
      let leftDown = 0;
      let left = Number(arr[i][j]) + Number(arr[i][j - 1]);
      // console.log(arr[i][j], arr[i - 1][j]);
      let leftUp = 0;

      if (j !== 0) {
        leftUp = Number(arr[i][j]) + Number(arr[i][j - 1]);
      }

      if (j !== m - 1) {
        leftDown = Number(arr[i][j]) + Number(arr[i][j + 1]);
      }

      // console.log(leftDown, left, leftUp);
      arr[i][j] = Math.max(leftDown, left, leftUp);
    }
  }

  console.log(arr);
  // return "";
}

export default goldMine;

